/// <reference types="react" />

import * as React from "react";

export interface onProps extends React.HTMLAttributes<HTMLElement> {}

export class on extends React.Component<onProps, any> {}

export interface onceProps extends React.HTMLAttributes<HTMLElement> {}

export class once extends React.Component<onceProps, any> {}

export interface offProps extends React.HTMLAttributes<HTMLElement> {}

export class off extends React.Component<offProps, any> {}

export interface emitProps extends React.HTMLAttributes<HTMLElement> {}

export class emit extends React.Component<emitProps, any> {}
export interface EventsProps extends React.HTMLAttributes<HTMLElement> {}

export default class Events extends React.Component<EventsProps, any> {
  static on: typeof on;
  static once: typeof once;
  static off: typeof off;
  static emit: typeof emit;
}
